// import { BoxInterface } from './interface.js'

/** Colored box presentation
 *
 * Props:
 * - id (unique)
 * - width
 * - height
 * - backgroundColor
 * - remove (function to call)
 *
 * BoxList -> Box
 */

// interface BoxInterface {
//   id: string;
//   width: number | 5;
//   height: number | 5;
//   backgroundColor: string;
//   remove: () => void;
// }

function Box( {id, width = 5, } ) {

  /** Remove a box. */
  function handleRemove() {
    box.remove(id);
  }

  return (
    <div className="Box">
      <div className="Box-box"
        style={{
          height: `${box.height}em`,
          width: `${box.width}em`,
          backgroundColor: box.backgroundColor
        }}
      />
      <button
        className="Box-removeBtn"
        onClick={handleRemove}>
        Remove The Box!
      </button>
    </div>
  );
}

export default Box;
